export default function charts(){const t=document.querySelector(".main-chart__container"),e=document.querySelector(".add-charts"),a={indexAxis:"y",layout:{padding:20},elements:{bar:{borderWidth:2}},scales:{x:{grid:{color:"#5B5B5B",tickColor:"#5B5B5B"}},y:{grid:{tickColor:"rgba(0,0,0,0)",color:"#5B5B5B"},ticks:{display:!1}}},responsive:!0,plugins:{legend:{position:"top"}}};t&&e&&fetch(chartData,{method:"GET",headers:{"content-type":"application/json"}}).then(t=>200!==t.status?Promise.reject():t.json()).then(o=>{const n=document.createElement("canvas");n.width=t.offsetWidth,n.height=t.offsetHeight,t.append(n);const s=n.getContext("2d");let c=[];o.groups.map(n=>{const s=document.createElement("div");s.classList.add("add-charts__item"),s.innerHTML='\n\t\t\t\t<div class="add-charts__item-inner">\n\t\t\t\t\t<div class="add-charts__item-container">\n\t\t\t\t\t\t<canvas></canvas>\n\t\t\t\t\t</div>\n\t\t\t\t</div>',e.append(s),new SimpleBar(s,{autoHide:!1});const d=s.querySelector("canvas");d.width=t.offsetWidth,d.height=t.offsetHeight;let r=[];n.data.map(t=>{r.push({label:t.name,data:[t.value],backgroundColor:t.color})});const i=d.getContext("2d"),l={type:"bar",data:{labels:[o.label],datasets:r},options:a};new Chart(i,l);n.data.map(t=>{c.push({label:t.name,data:[t.value],backgroundColor:t.color})})});const d={labels:[o.label],datasets:c};new Chart(s,{type:"bar",data:d,options:a})}).catch(t=>console.log(t))}